import { createSlice, createSelector } from "@reduxjs/toolkit"

const initialState = {
    topics: {id:0},
};


const topicsSlice = createSlice(
    {
        name: 'topicsSlice',
        initialState,
        reducers: {
            addTopic(state, action) {
                const { id, name, icon } = action.payload;
                let newTopic = {
                    id: id,
                    name: name,
                    icon: icon,
                    quizId: []


                }
                state.topics[id] = newTopic;
            }

        }
    }
);
const selectNestedTopics = createSelector(
    state => state.topics  // Assuming 'topics' is nested within 'topics' based on your initial state structure
);

export { selectNestedTopics };
export const { addTopic } = topicsSlice.actions;
export default topicsSlice.reducer;
